class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        k = -1
        for i, n in enumerate(nums):
            if n == 0:
                continue
            store = [False] * (n + 1)
            store[n] = True
            found = False
            for j, q in enumerate(queries):
                # print(f"Processing query {q} for index {i} with value {n}")
                if q[0] <= i <= q[1] and q[2] <= n:
                    val = q[2]
                    # print(f"Processing query {q} for index {i} with value {val}")
                    if store[val]:
                        found = True
                        k = max(k, j)
                        break
                    else:
                        for s in range(val, n + 1):
                            # print(s)
                            if store[s]:
                                store[s - val] = True
            if not found:
                return -1
        return k + 1

