class Solution {
public:
bool check(int i,int sum,vector<int> &v,vector<vector<int>> &dp){
    int n = v.size();
    if(sum == 0) return 1;
    if(i >= n) return 0;
    if(dp[i][sum] != -1) return dp[i][sum];
    int pick = 0,skip = 0;
    if(v[i] <= sum) pick = check(i+1,sum-v[i],v,dp);
    skip = check(i+1,sum,v,dp);
    return dp[i][sum] = pick | skip;
}

int sol(int k,vector<int> &a,vector<vector<int>>& q){
    int n = a.size() , m = q.size();
    vector<int> p(n+1);
    for(int i=0;i<=k;i++){
        int l=q[i][0],r = q[i][1],val = q[i][2];
        p[l] += val;
        p[r+1] -= val;
    }
    for(int i=1;i<=n;i++) p[i] += p[i-1];
    for(int i=0;i<n;i++){
        if(p[i] < a[i]) return 0;
        if(p[i] > a[i]){
            vector<int> v;
            for(int j=0;j<=k;j++){
                int l=q[j][0],r = q[j][1],val = q[j][2];
                if(l <= i && r >= i) v.push_back(val);
            }
            int sz = v.size();
            vector<vector<int>> dp(sz+1,vector<int>(a[i]+1,-1));
            if(!check(0,a[i],v,dp)) return 0;
        }
    }
    return 1;
}
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size() , m = q.size(), l = 0,r = m-1,ans = 1e9;

        bool f = 0;
        for(auto i : a) if(i != 0) f = 1;
        if(f == 0) return 0;

        while(l <= r){
            int mid = (l + r)/2;
            if(sol(mid,a,q)){
                ans = mid;
                r = mid - 1;
            }
            else l = mid + 1;
        }
        if(ans == 1e9) return -1;
        return ans+1;
    }
};

