class Solution
{
public:
    void rec(int ele, int pos, vector<vector<int>> &queries, bool taken, set<int> &ans, int idx)
    {
        if(ele < 0)
        {
            return;
        }
        DP[ele][pos][taken][idx] = true;
        if(ele == 0 and taken)
        {
            ans.insert(pos);
        }
        if(pos == queries.size()) return;
        int l = queries[pos][0], r = queries[pos][1], val = queries[pos][2];

        //take
        if(l <= idx and idx <= r)
        {
            if(ele - val >= 0 and not DP[ele - val][pos + 1][true][idx])
            rec(ele - val, pos + 1, queries, true, ans, idx);
        }
        if(not DP[ele][pos + 1][taken][idx])
            rec(ele, pos + 1, queries, taken, ans, idx);

    }
    int minZeroArray(vector<int> &nums, vector<vector<int>> &queries)
    {
        int N = len(nums);
        forn(i, 1001)
            forn(j, queries.size() + 1)
                forn(k, 2)
                    forn(x, N)
                        DP[i][j][k][x] = false;

        map<int, set<int>> ok;
        forn(i, N)
        {
            set<int> tmp;
            if(nums[i] == 0)
            {
                tmp.insert(0);
            }
            rec(nums[i], 0, queries, false, tmp, i);

            ok[i] = tmp;
        }
        // for(auto [k, vec]: ok)
        // {
        //     cout << k << " :";
        //     for(int i: vec)
        //         cout << i << ' ';
        //     cout << 'n';
        // }
        for(int i = 0; i <= queries.size(); i++)
        {
            bool uh = true;
            forn(j, N)
            {
                if(ok[j].empty())
                    return -1;
                auto iter = lower_bound(ok[j].begin(), ok[j].end(), i);
                if(iter != ok[j].end())
                {
                    uh &= ((*iter == i) or ((*ok[j].begin()) == 0));

                }

            }
            if(uh)
                    return i;
        }

        return -1;


    }
};

