import java.util.*;

class Solution {
    public boolean allZero(int nums[]) {
        int all = 1;
        for (int num : nums) {
            if (num != 0) {
                all = 0;
                break;
            }
        }
        if (all == 1) return true;
        return false;

    }
    public boolean check(int mid, int[] originalNums, int[][] queries) {
        int n = originalNums.length;
        int[] nums = new int[n]; 
        for(int i = 0; i < n; i++) {
            nums[i] = originalNums[i];
        }
        List<List<Integer>> dec = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            dec.add(new ArrayList<>());
        }

        for (int i = 0; i <= mid; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for (int j = l; j <= r; j++) {
                dec.get(j).add(val);
            }
        }

        for (int i = 0; i < n; i++) {
            if (!subset_prob(nums[i], dec.get(i))) {
                return false;
            }
        }
        return true;
    }

    public boolean subset_prob(int target, List<Integer> dec) {
        if (target == 0) return true;
        if (dec.isEmpty()) return false;

        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int val : dec) {
            for (int j = target; j >= val; j--) {
                dp[j] |= dp[j - val];
            }
        }
        return dp[target];
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int lo = 0, hi = queries.length - 1;
        int ans = -1;

        if(allZero(nums)) return 0;

        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            if (check(mid, nums, queries)) {
                ans = mid + 1;
                hi = mid - 1; 
            } 
            else {
                lo = mid + 1;
            }
        }
        return ans;
    }
}

