class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        map<int, vector<int>> m;
        int ans = -1;
        const int INF = 1000000000;
        for(auto &q : queries) {
            for (int j = 0; j < q[0]; j++) m[j].push_back(0);
            for (int j = q[0]; j <= q[1]; j++) m[j].push_back(q[2]);
            for (int j = q[1] + 1; j < nums.size(); j++) m[j].push_back(0);
        }
        for (int i = 0; i < nums.size(); i++) {
            vector<int>& v = m[i];
            int target = nums[i];
            int L = v.size();
            int best = INF;
            if(target == 0) {
                best = 0;
            } else {
                vector<bool> dp(target + 1, false);
                dp[0] = true;
                for (int pos = 0; pos < L; pos++) {
                    vector<bool> ndp = dp;
                    for (int s = 0; s <= target; s++) {
                        if(dp[s] && s + v[pos] <= target)
                            ndp[s + v[pos]] = true;
                    }
                    dp = ndp;
                    if(dp[target]) {
                        best = pos + 1;
                        break;
                    }
                }
            }
            ans = max(ans, best);
        }
        return (ans == INF ? -1 : ans);
    }
};

