class Solution {
public:
    bool solve(int n, int target, vector<int>& nums, vector<vector<int>> &dp)
    {
        if(target == 0)
        {
            return true;
        }
        if(n == 0)
        {
            return target == nums[0];
        }
        if(dp[n][target] != -1)
        {
            return dp[n][target];
        }
        bool nottake = solve(n-1, target, nums, dp);
        bool take = false;
        if(nums[n] <= target)
        {
            take = solve(n-1, target - nums[n], nums,dp);
        }
        dp[n][target] = take || nottake;
        return dp[n][target];
    }
    bool check(vector<int>& nums, vector<vector<int>>& queries, int mid)
    {
        int n = nums.size();
        vector<vector<int>> temp(n);
        for (int i = 0; i < mid; i++)
        {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int i = l; i <= r; i++)
            {
                temp[i].push_back(val);
            }
        }
        for(int i = 0; i < n; i++)
        {
            if(temp[i].empty())
            {
                if(nums[i] != 0)
                {
                    return false;
                }
                else
                {
                    continue;
                }
            }
            vector<vector<int>> dp(temp[i].size(), vector<int> (nums[i] + 1,-1));
            if(!solve(temp[i].size() - 1, nums[i], temp[i], dp))
            {
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0;
        int h = queries.size();
        int ans = -1;
        while(l <= h)
            {
                int mid = (l + h)/2;
                if(check(nums, queries, mid))
                {
                    ans = mid;
                    h = mid - 1;
                }
                else
                {
                    l = mid + 1;
                }
            }
        return ans;
    }
};

