class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        largest = max(nums)
        if all(num == 0 for num in nums):
            return 0

        dp = [[False] * (largest + 1) for _ in range(n)]
        for i in range(n):
            dp[i][0] = True

        for query_index in range(len(queries)):
            l, r, val = queries[query_index]

            for i in range(l, r + 1):
                for sub in range(largest, val - 1, -1):
                    if dp[i][sub - val]:
                        dp[i][sub] = True

            success = True
            for j in range(n):
                if not dp[j][nums[j]]:
                    success = False
                    break

            if success:
                return query_index + 1

        return -1

