class Solution {
public:

    bool rec(int idx, int sum, vector<int>& v, vector<vector<int>>& dp){
        int n = v.size();
        if(sum == 0)
            return 1;
        if(idx == n || sum < 0)
            return 0;
        if(dp[idx][sum] != -1)
            return dp[idx][sum];
        else
            return dp[idx][sum] = rec(idx+1,sum-v[idx],v,dp)|rec(idx+1,sum,v,dp);
    }
    bool check(int q, vector<int>& nums, vector<vector<int>>& queries){
        int n = nums.size();
        vector<vector<int>> u(n);
        for(int i = 0; i < q; i++){
            for(int j = queries[i][0]; j <= queries[i][1]; j++)
            u[j].push_back(queries[i][2]);
        }
        vector<vector<int>> dp;
        for(int i = 0; i < n; i++){
            dp.resize(u[i].size(), vector<int> (nums[i]+1,-1));
            if(!rec(0, nums[i], u[i], dp))
            return 0;
            dp.clear();
        }
        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int start = 0, end = q, ans = -1;
        while(start <= end){
            int mid = end-(end-start)/2;
            if(check(mid, nums, queries)){
                ans = mid;
                end = mid-1;
            }
            else
            start = mid+1;
        }
        return ans;
    }
};

