class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();

        int l = 0, r = q + 1; 
        while(l < r) {
            int mid = l + (r - l) / 2;
            if(check(nums, queries, mid)) r = mid;
            else l = mid + 1;
        }
        return (l > q ? -1 : l);
    }
private:
    bool check(const vector<int>& nums, const vector<vector<int>>& qs, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0)
                continue;
            vector<int> coins;
            for (int j = 0; j < k; j++) {
                int l = qs[j][0], r = qs[j][1], v = qs[j][2];
                if (i >= l && i <= r)
                    coins.push_back(v);
            }

            bitset<1001> dp;
            dp.reset();
            dp[0] = 1;
            for (int c : coins) {
                dp |= (dp << c);
            }
            if (!dp[target]) return false;
        }
        return true;
    }
};

