class Solution {
private:
    int solveForMinQueries(int ind, int n, vector<vector<int>>& queries, int val, int index, vector<vector<int>>& dp){
        if(ind == n){
            if(val == 0){
                return n - 1;
            }
            return INT_MAX;
        }
        if(val == 0){
            return max(ind - 1, 0);
        }
        if(dp[ind][val] != -1){
            return dp[ind][val];
        }
        int mini = INT_MAX;
        mini = min(mini, solveForMinQueries(ind + 1, n, queries, val, index, dp));
        if(index >= queries[ind][0] && index <= queries[ind][1] && (val - queries[ind][2]) >= 0){
            mini = min(mini, solveForMinQueries(ind + 1, n, queries, val - queries[ind][2], index, dp));
        }
        return dp[ind][val] = mini;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int i,j,k;
        bool flag = true;
        for(i=0;i<nums.size();i++){
            if(nums[i] != 0){
                flag = false;
                break;
            }
        }
        if(flag){
            return 0;
        }
        vector<vector<int>> dp(queries.size(), vector<int> (1001, -1));
        int maxi = 0;
        for(i=0;i<nums.size();i++){
            maxi = max(maxi, solveForMinQueries(0, queries.size(), queries, nums[i], i, dp));
            for(j=0;j<queries.size();j++){
                for(k=0;k<1001;k++){
                    dp[j][k] = -1;
                }
            }    
        }
        if(maxi == INT_MAX){
            return -1;
        }
        return maxi + 1;
    }
};

