class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int rst = 0;
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] != 0) {
                int[][] memo = new int[nums[i] + 1][queries.length];
                int v = helper(nums[i], i, queries, 0, memo);
                if (v == -1) {
                    return -1;
                }
                rst = Math.max(rst, v + 1);
            }
        }

        return rst;
    }
    private int helper(int val, int id1, int[][] queries, int index, int[][] memo) {
        if (index >= queries.length && val != 0) {
            return -1;
        }
        if (id1 < queries[index][0] || id1 > queries[index][1]) {
            return helper(val, id1, queries, index + 1, memo);
        }

        if (val < queries[index][2]) {
            return helper(val, id1, queries, index + 1, memo);
        }
        if (val == queries[index][2]) {
            return index;
        }
        if (memo[val][index] != 0) {
            return memo[val][index];
        }

        int first = helper(val, id1, queries, index + 1, memo);
        if (first == -1) {
            int a = helper(val - queries[index][2], id1, queries, index + 1, memo);
            memo[val][index] = a;
            return a;
        }

        int second = helper(val - queries[index][2], id1, queries, index + 1, memo);
        if (second == -1) {
            memo[val][index] = first;
            return first;
        }

         int a = Math.min(first, second);
        memo[val][index] = a;
        return a;

    }
}

