class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        auto subset_sum = vector<vector<bool>>(n, vector<bool>(1001, false));
        auto subset_sum_prev = vector<vector<bool>>(n, vector<bool>(1001, false));
        for (auto i = 0; i < n; ++i) {
            subset_sum[i][0] = true;
            subset_sum_prev[i][0] = true;
        }

        auto cnt = 0;

        auto complete = true;
        for (auto i = 0; i < n; ++i) {
            if (!subset_sum[i][nums[i]]) {
                complete = false;
                break;
            }
        }
        if (complete) return cnt;

        for (auto& q: queries) {
            ++cnt;

            for (auto j = q[0]; j <= q[1]; ++j) {
                for (auto k = 0; k + q[2] <= 1000; ++k) {
                    subset_sum[j][k+q[2]] = 
                        subset_sum[j][k+q[2]] || subset_sum_prev[j][k];
                }
                for (auto k = 0; k <= 1000; ++k) {
                    subset_sum_prev[j][k] = subset_sum[j][k];
                }
            }
            auto complete = true;
            for (auto i = 0; i < n; ++i) {
                if (!subset_sum[i][nums[i]]) {
                    complete = false;
                    break;
                }
            }
            if (complete) return cnt;
        }

        return -1;
    }
};

