class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ans=-1
        not_pos=False
        for i in range(len(nums)):
            arr=[]
            indices=[]
            num=nums[i]
            for j in range(len(queries)):
                l, r, v = queries[j]
                if i>=l and i<=r:
                    arr.append(v)
                    indices.append(j)
            k=self.isSubsetSum(arr,num)
            if k!=-1:
                if k==0:
                    ans=max(ans,0)
                else:
                    x=indices[k-1]
                    ans=max(ans,x+1)
            else:
                not_pos=True
        if not_pos:
            return -1
        return ans    

    def isSubsetSum(self,arr, sum):
        n = len(arr)

        dp = [[False] * (sum + 1) for _ in range(n + 1)]

        for i in range(n + 1):
            dp[i][0] = True

        for i in range(1, n + 1):
            for j in range(1, sum + 1):
                if j < arr[i - 1]:  
                    dp[i][j] = dp[i - 1][j]
                else:                    
                    dp[i][j] = dp[i - 1][j] or dp[i - 1][j - arr[i - 1]]


        for i in range(n+1):
            if dp[i][sum]:
                return i
        return -1

