                # print('target is 0')
                return True

            total = 0
            for v, freq in counter.items():
                total += v * freq
            if total < target:
                # print('total < target', [total, target])
                return False

            dp = set([0])
            for v, freq in counter.items():
                new_dp = dp
                for f in range(1, freq + 1):
                    new_dp = new_dp | set(x + f * v for x in dp)
                    # print(new_dp)
                    if target in new_dp:
                        return True
                dp = new_dp
            return False


        pre = Counter()
        diff_add = [Counter() for _ in range(size + 1)]
        diff_rmv = [Counter() for _ in range(size + 1)]

        for i, n in enumerate(nums):
            pre = pre + diff_add[i] - diff_rmv[i]

            res = False
            while qi < q_size + 1:
                res = check(pre, n)
                if res or qi == q_size:
                    break

                q1, q2, val = queries[qi]
                if i < q1:
                    diff_add[q1][val] += 1
                    diff_rmv[q2 + 1][val] += 1
                elif q1 <= i <= q2:
                    pre[val] += 1 
                    diff_rmv[q2 + 1][val] += 1
                qi += 1
                # print(['update', pre, qi])

            if not res:
                return -1

            # if pre < n or pre > n:
            #     return -1

        return qi

