class Solution {
public:
    int solve(vector<pair<int,int>>& arr,int val,int n,vector<vector<int>>& dp){
        if(n==arr.size()){
            return INT_MAX;
        }
        if(arr[n].first==val){
            return arr[n].second;
        }
        if(dp[n][val]!=-1){
            return dp[n][val];
        }
        int ans=INT_MAX;
        ans=solve(arr,val,n+1,dp);
        if(arr[n].first<=val){
            int value=solve(arr,val-arr[n].first,n+1,dp);
            ans=min(ans,value);
        }
        // int m=arr.size();
        // vector<vector<int>> dp(m+1,vector<int>(val+1,INT_MAX));
        // if(arr[1].first<=val)dp[1][arr[1].first]=arr[1].second;
        // for(int i)
        return dp[n][val]=ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        vector<vector<pair<int,int>>> arr(n);
        for(int i=0;i<n;i++){
            for(int j=0;j<queries.size();j++){
                int l=queries[j][0];
                int r=queries[j][1];
                if(i>=l && i<=r){
                    arr[i].push_back({queries[j][2],j});
                }
            }
        }
        int mx=-1;
        for(int i=0;i<n;i++){
            if(nums[i]==0)continue;
            int k=arr[i].size();
            vector<vector<int>> dp(k+1,vector<int>(nums[i]+1,-1));
            int v=solve(arr[i],nums[i],0,dp);
            if(v==INT_MAX)return -1;
            mx=max(mx,v);

        }
        return mx+1;

    }
};

