class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        auto valid = [&](int m) -> bool {
            vector<vector<int>> values(n + 1);
            for (int i = 0; i < m; i++) {
                vector<int>& q = queries[i];

                for (int j = q[0]; j <= q[1]; j++)
                    values[j].push_back(q[2]);
            }

            for (int i = 0; i < n; i++) {
                if (nums[i] == 0)
                    continue;

                vector<int>& options = values[i];
                vector<bool> dp (nums[i] + 1, false);
                dp[0] = true;

                for (int option : options) 
                    for (int x = nums[i]; x >= option; x--)
                        if (dp[x - option])
                            dp[x] = true;

                if (!dp[nums[i]])
                    return false;
            }

            return true;
        };

        int l = 0; 
        int r = m;
        int best = -1;

        while (l <= r) { 
            int m = (l + r) / 2;

            if (valid(m)) {
                r = m - 1;
                best = m;
            } else 
                 l = m + 1;
        }

        return best; 
    }
};

