class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qu) {
        int l,r,mid,i,n=nums.size(),q=qu.size();
        vector<vector<int>> dp(n,vector<int>(1001,q+1));
        for(i=0;i<n;i++){
            dp[i][0]=0;
        }
        for(i=0;i<q;i++){
            int a = qu[i][0];
            int b = qu[i][1];
            int val = qu[i][2];
            for(l = a;l<=b;l++){
                for(mid = 1000-val;mid>=0;mid--){
                    if(dp[l][mid]<(q+1)){
                        dp[l][mid+val] = min(dp[l][mid+val], i+1);
                    }
                }
            }
        }
        int ans = -1;
        for(i=0;i<n;i++){
            ans = max(ans, dp[i][nums[i]]);
        }
        if(ans>q) return -1;
        return ans;
    }
};

