class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        matrix = [[] for _ in range(len(nums))]
        for j, [l, r, v] in enumerate(queries):
            for i in range(l, r + 1):
                matrix[i].append((v, j + 1))
        def backtrack(nums, index, cur_sum, target):
            if cur_sum == target:
                return nums[index - 1][1]
            if index == len(nums):
                return inf
            res = min(backtrack(nums, index + 1, cur_sum, target), backtrack(nums, index + 1, cur_sum + nums[index][0], target))
            return res
        # print(matrix)
        res = -1
        for i, array in enumerate(matrix):
            target = nums[i]
            if target == 0:
                res = max(res, 0)
                continue
            @cache
            def recur(index, cur_sum):
                if cur_sum == target:
                    return array[index - 1][1]
                if index == len(array):
                    return inf
                return min(recur(index + 1, cur_sum + array[index][0]), recur(index + 1, cur_sum))
            tmp = recur(0, 0)
            # print(array, tmp)
            if tmp == inf:
                return -1
            res = max(res, tmp)
        return res

