class Solution {
public:
    const int INF = 1e6;
    int dp[1001][1001];
    int solve(int i, int target, vector<vector<int>> &arr) {
        int n = arr.size();
        if(target < 0) return INF;
        if(i >= n) return target == 0 ? 0 : INF;
        if(dp[i][target] != -1) return dp[i][target];
        return dp[i][target] = min(solve(i+1,target,arr),max(arr[i][1],solve(i+1,target-arr[i][0],arr)));
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<vector<int>>> list(n);
        for(int i = 0; i < m; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for(int j = l; j <= r; j++)
                list[j].push_back({val,i});
        }
        int ans = 0;
        for(int i = 0; i < n; i++) {
            memset(dp,-1,sizeof(dp));
            if(nums[i] == 0) continue;
            ans = max(ans,1+solve(0,nums[i],list[i]));
        }
        return ans >= INF ? -1 : ans;
    }
};

