class Solution {
public:
    int dp[1005][1005];

    int poss(int sum, vector<vector<int>>& queries, int n, int ind,int ind1) {
        if (sum < 0) return 0;
        if (sum == 0) return 1;
        if (ind >= n) return 0; 
        if (dp[ind][sum] != -1) return dp[ind][sum];
        int ans = 0;
        if(ind1>=queries[ind][0] && ind1 <= queries[ind][1])
        ans = (poss(sum - queries[ind][2], queries, n, ind + 1,ind1));
        ans= (ans|poss(sum, queries, n, ind + 1,ind1));
        return dp[ind][sum] = ans;
    }

    bool possible(int n, vector<vector<int>>& queries, vector<int>& nums) {
        for (int i = 0; i < nums.size(); i++) { 
            memset(dp, -1, sizeof(dp)); 
            if (!poss(nums[i], queries, n, 0,i)) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int start = 0, end = queries.size(), mid;

        while (start < end) { 
            mid = (start + end) / 2;
            if (possible(mid, queries, nums)) {
                end = mid;
            } else {
                start = mid + 1;
            }
        }

        return (possible(start, queries, nums)) ? start : -1;
    }
};

