class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        memo = {}
        def canAdd(i: int, num: int, change: List[int], counts: Dict[int, int]) -> bool:
            # print(i, num, change, counts, memo)
            if num == 0:
                return True
            elif num < 0 or i == len(change) or change[i] > num:
                return False
            key = (i, num)
            if key in memo:
                return memo[key]

            for take in range(counts[change[i]], -1, -1):
                memo[key] = canAdd(i + 1, num - (change[i] * take), change, counts)
                if memo[key]:
                    return True
            return False

        def valid(k: int) -> bool:
            add = [[] for _ in range(len(nums))]
            sub = [[] for _ in range(len(nums))]
            for i in range(k):
                query = queries[i]
                add[query[0]].append(query[2])
                if query[1] < len(nums) - 1:
                    sub[query[1] + 1].append(query[2])

            # print('aaa', k, add, sub)
            changes = {}
            for i in range(len(nums)):
                for j in add[i]:
                    changes[j] = changes.get(j, 0) + 1
                for j in sub[i]:
                    changes[j] -= 1
                    if changes[j] == 0:
                        changes.pop(j)

                nonlocal memo
                memo = {}
                if not canAdd(0, nums[i], sorted(changes.keys()), changes):
                    return False
            # print(memo)
            return True

        if not valid(len(queries)):
            return -1

        left = 0
        right = len(queries)
        while left < right:
            # memo = {}
            mid = (left + right) // 2
            if valid(mid):
                right = mid
            else:
                left = mid + 1

        return left


