    bool chk2(int i, vector<int>& nums, map<int,vector<int>>& mp, int curr, int j, vector<vector<int>>& memo) {
        if (curr == nums[i]) return true;
        if (j >= mp[i].size()) return false;

        if (memo[curr][j] != -1) return memo[curr][j];

        bool take = false;
        if (curr + mp[i][j] <= nums[i]) {
            take = chk2(i, nums, mp, curr + mp[i][j], j + 1, memo);
        }
        bool not_take = chk2(i, nums, mp, curr, j + 1, memo);

        return memo[curr][j] = (take || not_take);
    }
    bool chk(vector<int>& nums, map<int,vector<int>>& mp){
        for(int i=0;i<nums.size();i++){
            vector<vector<int>> memo(1001, vector<int>(mp[i].size(), -1));
            if(!chk2(i,nums,mp,0,0,memo)){
                return 0;
            }
        }
        return 1;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) {
    return 0;
}
        int ans=-1;
        int l=0,h=queries.size()-1;
        while(l<=h){
            int m=l+((h-l)/2);
            map<int,vector<int>>mp;
            for(int j=0;j<=m;j++){
                for(int i=queries[j][0];i<=queries[j][1];i++){
                    mp[i].push_back(queries[j][2]);
                }
            }

            if(chk(nums,mp)){
                ans=m+1;h=m-1;
            }
            else l=m+1;
        }
        return ans;
    }
};

