class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        auto ok = [&](int m) {
            vector<vector<int>> a(n, vector<int> (11, 0)), dp(11, vector<int> (1005, -1));

            for(int id = 0; id <= m; id++) {
                int l = queries[id][0], r = queries[id][1], val = queries[id][2];
                for(int i = l; i <= r; i++) a[i][val]++;
            }

            auto f = [&](int i, int tar, int id, auto && f) -> int {
                if(tar == 0) return 1;
                if(i == 11) return 0;

                if(dp[i][tar] != -1) return dp[i][tar];

                int ans = 0;
                for(int j = 0; j <= a[id][i]; j++) {
                    int s = j * i;
                    if(tar - s >= 0) ans = max(ans, f(i + 1, tar - s, id, f));
                    else break;
                }

                return dp[i][tar] = ans;
            };

            for(int i = 0; i < n; i++) {
                dp.assign(11, vector (1005, -1));
                if(!f(0, nums[i], i, f)) return 0;
            }

            return 1;
        };

        if(*max_element(nums.begin(), nums.end()) == 0) return 0;

        int l = 0, r = queries.size() - 1, ans = -1;
        while(l <= r) {
            int m = (l + r) / 2;
            if(ok(m)) ans = m + 1, r = m - 1;
            else l = m + 1;
        }
        return ans;
    }
};

