class Solution {
    int dfs(int v, vector<pair<int, int>>& val, int i, vector<vector<int>>& dp)
    {
        if (i == size(val))
            return 1e9;

        if (dp[v][i] != -1)
            return dp[v][i];

        auto [vv, ii] = val[i];

        if (v == vv)
            return ii + 1;

        int dont = dfs(v, val, i + 1, dp);
        int take = v > vv ? dfs(v - vv, val, i + 1, dp) : 1e9;

        return dp[v][i] = min(dont, take);
    }

public:
