        {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];

            for (int j = l; j <= r; ++j)
                val[j].push_back({v, i});
        }

        #if 0
        for (int i = 0; i < n; ++i)
        {
            cout << nums[i] << ": ";

            for (auto [vv, ii] : val[i])
                cout << vv << "," << ii << " ";

            cout << "n";
        }
        #endif

        int ans = 0;

