/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function(nums, queries) {
  let n = nums.length, q = queries.length;
  const vals = Array.from({length: n}, () => []);

  let flag = false;
  for (let j = 0; j < n; j++) {
    if (nums[j] !== 0) {
      flag = true;
      break;
    }
  }
  if (!flag)
    return 0;
  for (let i = 0; i < q; i++) {
    for (let k = queries[i][0]; k <= queries[i][1]; k++) {
      vals[k].push([queries[i][2], i]);
    }
  }

  function solve(idx) {
    let target = nums[idx];
    const mem = Array.from({length: vals[idx].length + 1}, () => new Array(vals[idx].length + 1));
    function rec(i = 0, cur = 0, lastUsed = -1) {
      // console.log(cur)
      if (cur > target)
        return Infinity;
      if (cur === target)
        return lastUsed;
      if (i >= vals[idx].length)
        return Infinity;
      if (mem[i][cur] !== undefined)
        return mem[i][cur];

      let res1 = rec(i + 1, cur + vals[idx][i][0], vals[idx][i][1]);
      let res2 = rec(i + 1, cur, lastUsed);
      return mem[i][cur] = Math.min(res1, res2);
    }
    // console.log(target, vals[idx], rec() + 1);
    return rec() + 1;
  }

  let res = 0;
  for (let i = 0; i < n; i++) {
    res = Math.max(res, solve(i));
    if (!Number.isFinite(res))
      return -1;
  }
  return res;
};

