        int nflg = 0;
        for(int i  = 0 ; i < l; i++){
            if(nums[i] != 0){
                nflg = 1;
            }
        }
        if(nflg == 0) return 0;
        int ans = -1;
        for (int i = 0; i < l; i++) {
            int wt = nums[i];
            if(wt == 0) continue;
            vector<int> ind;
            vector<int> temp;
            for (int j = 0; j < n; j++) {
                if (que[j][0] <= i && que[j][1] >= i) {
                    temp.push_back(que[j][2]);
                    ind.push_back(j);
                }
            }
            if(temp.size() == 0) return -1;
            // for(auto j : ind) cout << j << " ";
