class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);

        bool allZero = true;
        for (int j = 0; j < n; j++) {
            dp[j].set(0);
            if (nums[j] != 0) allZero = false;
        }

        if (allZero) return 0;

        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];

            for (int j = l; j <= r; j++) {
                dp[j] |= (dp[j] << v);
                dp[j] &= bitset<1001>().set() >> (1001 - nums[j] - 1);
            }

            bool allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j][nums[j]]) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }
        return -1;
    }
};

