class Solution {
public:
int subsetSumHelper(int idx, int currSum, int n, vector<int>& v, vector<vector<int>>& dp) {
    if (currSum == 0) return 1;
    if (currSum < 0 || idx == n) return 0;
    if (dp[idx][currSum] != -1) return dp[idx][currSum];

    int ans = subsetSumHelper(idx + 1, currSum, n, v, dp);
    ans |= subsetSumHelper(idx + 1, currSum - v[idx], n, v, dp);

    return dp[idx][currSum] = ans;
}
int helper(vector<int>& v, vector<vector<int>>& queries, int mid) {
    int n = v.size();
    vector<vector<int>> temp(n);

    for (int i = 0; i < n; i++) 
        temp[i].push_back(0);

    for (int i = 0; i < mid; i++) {
        int u = queries[i][0], v = queries[i][1], val = queries[i][2];
        for (int j = u; j <= v; j++) 
            temp[j].push_back(val);
    }

    bool flag = true;
    for (int i = 0; i < n; i++) {
        int len = temp[i].size();
        vector<vector<int>> dp(len + 1, vector<int>(1001, -1));
        int canAchieveSum = subsetSumHelper(0, v[i], len, temp[i], dp);
        if (canAchieveSum == 0) {
            flag = false;
            break;
        }
    }
    return flag;
}

int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    int low = 0 , high = queries.size() , mid ; 
    int ans = 1e9 ; 
    while(low <= high){
        mid = (low + high) / 2 ; 
        int temp = helper(nums , queries , mid) ; 
        if(temp){
            ans = min(ans , mid) ; 
            high = mid - 1 ; 
        }
        else{
            low = mid + 1 ; 
        }
    }
    if(ans == 1e9)
        ans = -1 ; 
    return ans ;        
}

};

