class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        c = (1 << n) - 1
        cur = 0
        possible_values = [set([0]) for _ in range(n)]
        for i in range(n):
            if nums[i] in possible_values[i]:
                cur |= (1 << i)
        if cur == c: return 0
        for i in range(len(queries)):
            l, r, val = queries[i]
            for j in range(l, r + 1):
                if (1 << j) & cur != 0: continue
                pv = possible_values[j]
                for k in list(pv):
                    pv.add(k + val)
                if nums[j] in pv: cur |= (1 << j)
            if cur == c: return (i + 1)
        return -1

