class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0

        answer = -float("inf")
        for i in range(len(nums)):
            queries_so_far = []
            found = False

            for j in range(len(queries)):
                left, right, value = queries[j]

                if left <= i <= right:
                    queries_so_far.append(value)

                    can_use_queries = self.subset_possible(queries_so_far, nums[i])

                    if can_use_queries:
                        answer = max(answer, j+1)
                        found = True
                        break

            if not found:
                return -1

        return answer if answer != -float("inf") else -1 

    def subset_possible(self, array, target):

        @cache
        def recurse(i, target):
            if target == 0:
                return True
            elif target < 0:
                return False
            elif i == len(array):
                return False

            return recurse(i+1, target) or recurse(i+1, target - array[i])

        return recurse(0, target)

