import java.util.*;

public class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = new boolean[n][1001];

        int j = 0;
        while (j < n) {
            dp[j][0] = true;
            j++;
        }

        j = 0;
        boolean allZero = true;
        while (j < n) {
            if (nums[j] != 0) {
                allZero = false;
                break;
            }
            j++;
        }
        if (allZero) return 0;

        int qIndex = 0;
        while (qIndex < queries.length) {
            int l = queries[qIndex][0];
            int r = queries[qIndex][1];
            int v = queries[qIndex][2];

            int index = l;
            while (index <= r) {
                boolean[] shifted = new boolean[1001];
                int i = 0;
                while (i < 1001 - v) {
                    if (dp[index][i]) {
                        shifted[i + v] = true;
                    }
                    i++;
                }
                i = 0;
                while (i < 1001) {
                    dp[index][i] = dp[index][i] || shifted[i];
                    i++;
                }
                i = nums[index] + 1;
                while (i < 1001) {
                    dp[index][i] = false;
                    i++;
                }
                index++;
            }

            boolean allAchieved = true;
            j = 0;
            while (j < n) {
                if (!dp[j][nums[j]]) {
                    allAchieved = false;
                    break;
                }
                j++;
            }
            if (allAchieved) return qIndex + 1;
            qIndex++;
        }
        return -1;
    }
}

