class Solution {
private:
    static const int MAX_N = 10 + 5;
    static const int MAX_M = 1000 + 5;

    vector <int> offsets[MAX_N];
    bool dp[MAX_M];

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();

        for(int i = 0; i < queries.size(); i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];
            for(int j = 0; j < n; j++){
                if(l <= j && j <= r){
                    offsets[j].push_back(v);
                }else{
                    offsets[j].push_back(0);
                }
            }
        }
        int answer = 0;
        for(int _ = 0; _ < n; _++){
            int x = nums[_];
            for(int i = 0; i < MAX_M; i++){
                dp[i] = false;
            }
            dp[0] = true;
            if(dp[x] == true){
                answer = max(answer, 0);
                continue;
            }
            for(int i = 0; i < offsets[_].size(); i++){
                int v = offsets[_][i];
                for(int j = MAX_M - 1 - v; j >= 0; j--){
                    dp[j + v] |= dp[j];
                }
                if(dp[x] == true){
                    answer = max(answer, i+1);
                    break;
                }
            }
            if(dp[x] == false){
                return -1;
            }
        }
        return answer;
    }
};

