class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        int l = 0 , h = queries.length;
        int res = -1;
        while(l<=h){
            int mid = (l+h)/2;
            if(possible(mid,nums,queries)) {
                res = mid;
                h=mid-1;
            } else l=mid+1;
        }
        return res;


    }
    public boolean possible(int mid,int nums[],int[][] queries){
        int n = nums.length;
        List<Integer> val[]=new ArrayList[n];
        for(int i = 0;i<n;i++) val[i]=new ArrayList<>();
        for(int i=0;i<mid;i++){
            int q[]=queries[i];
            int l = q[0], r=q[1], value = q[2];
            for(int j=l;j<=r;j++) val[j].add(value);
        }
        for(int i=0;i<n;i++){
            if(!sumPossible(nums[i],val[i])) return false;
        }
        return true;
    }
    public boolean sumPossible(int sum ,List<Integer> nums){

        boolean arr[]=new boolean[sum+1];
        arr[0]=true;
        for(int val : nums){
            for(int i=sum-val;i>=0;i--) {
                if(arr[i]) arr[i+val]=true;
            }
        }
        return arr[sum];
    }
}

