import java.util.AbstractMap;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        HashMap<Integer, List<Map.Entry<Integer, Integer>>> table = new HashMap<>();

        for (int j = 0; j < nums.length; j++) {
            List<Map.Entry<Integer, Integer>> list = new ArrayList<>();
            for (int i = 0; i < queries.length; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];
                if (j >= l && j <= r) {
                    list.add(new AbstractMap.SimpleEntry<>(val, i));
                }
            }
            table.put(j, list);
        }

        int result = Integer.MIN_VALUE;

        for (Map.Entry<Integer, List<Map.Entry<Integer, Integer>>> entry : table.entrySet()) {
            // solve this problem

            // 17     1 3 5 2 10 1

            // select min k first elements to make the sum 17
            int[][] dp = new int[entry.getValue().size()][nums[entry.getKey()] + 1];
            for (int[] arr: dp) Arrays.fill(arr, -1);
            if (nums[entry.getKey()] == 0) {
                result = Math.max(result, 0);
                continue;
            }
            int subproblemIndex = solve(0, nums[entry.getKey()], entry.getValue(), dp);
            if (subproblemIndex == Integer.MAX_VALUE) return -1;
            else result = Math.max(result, subproblemIndex + 1);
        }
        return result;
    }

    // value and index
    int solve(int i, int target, List<Map.Entry<Integer, Integer>> arr, int[][] dp) {
        if (i == arr.size()) return Integer.MAX_VALUE;
        if (target < 0) return Integer.MAX_VALUE;

        if (dp[i][target] != -1) return dp[i][target];
        if (target == arr.get(i).getKey()) {
            dp[i][target] = arr.get(i).getValue();
            return dp[i][target];
        }

        int take = solve(i + 1, target - arr.get(i).getKey(), arr, dp);
        int notTake = solve(i + 1, target, arr, dp);

        dp[i][target] = Math.min(take, notTake);
        return dp[i][target];
    }
}

