class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<pair<int, int>>> coverage(n);
        for (int j = 0; j < q; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; ++i) {
                coverage[i].emplace_back(j, val);
            }
        }

        vector<vector<int>> prefix_sums(n);
        vector<vector<bitset<1001>>> dp(n);

        for (int i = 0; i < n; ++i) {
            int m = coverage[i].size();
            vector<int> prefix(m + 1, 0);
            vector<bitset<1001>> dp_i(m + 1);

            dp_i[0][0] = true;

            for (int s = 1; s <= m; ++s) {
                int val = coverage[i][s-1].second;
                prefix[s] = prefix[s-1] + val;
                dp_i[s] = dp_i[s-1] | (dp_i[s-1] << val);
            }

            prefix_sums[i] = prefix;
            dp[i] = dp_i;
        }

        int left = 0, right = q;
        int ans = -1;

        while (left <= right) {
            int mid = (left + right) / 2;
            bool valid = true;

            for (int i = 0; i < n; ++i) {
                int target = nums[i];
                if (target == 0) continue;

                auto& cov = coverage[i];
                int pos = upper_bound(cov.begin(), cov.end(), make_pair(mid-1, 0), 
                    [](const pair<int, int>& a, const pair<int, int>& b) {
                        return a.first < b.first;
                    }) - cov.begin();

                int sum = prefix_sums[i][pos];
                if (sum < target) {
                    valid = false;
                    break;
                }
                if (sum == target) {
                    continue;
                }
                if (!dp[i][pos][target]) {
                    valid = false;
                    break;
                }
            }
            if (valid) {
                ans = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        if(ans !=-1) return ans;
        else return -1;
    }
};

