class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        pair<vector<int>,vector<vector<int>>>p={nums,queries};
        vector<bitset<1002>>dp(n);
        bool f=1;
        for(int i=0;i<n;i++){
            dp[i].reset();
            dp[i].set(0,true);
        }
        for(int i=0;i<n;i++){
            if(nums[i]!=0){
                f=0;
                break;
            }
        }
        if(f)return 0;
        for(int i=0;i<queries.size();i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                dp[j]=dp[j]|(dp[j]<<queries[i][2]);
                for(int k=nums[j]+1;k<1001;k++)dp[j].reset(k);
            }
            bool curr=true;
            for(int j=0;j<n;j++){
                if(!dp[j].test(nums[j])){
                    curr=false;
                    break;
                }
            }
            if(curr)return i+1;
        }
        return -1;
    }
};

