class Solution {
public:
    int MAX = 100000;
    int solve( int n,int idx, vector<int>&arr, vector<vector<int>> &dp ) {
        if( idx == arr.size() )
            return MAX;
        if( n - arr[idx] == 0 )
            return idx;
        if( dp[n][idx] != -1 )
            return dp[n][idx];
        int a=MAX,b;
        if( n - arr[idx] > 0 )
            a = solve( n - arr[idx], idx+1, arr, dp  );
        b = solve( n, idx+1, arr, dp );
        return dp[n][idx] = min( a,b );
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> final(nums.size(),-1);
        for( int i=0; i<nums.size(); ++i ) {
            if( nums[i] == 0 ) {
                final[i] = 0;
                continue;
            }
            int n = nums[i];
            vector<int> indexToNumQ;
            vector<int> arr;
            for( int  j=0; j< queries.size(); ++j ) {
                // building arr and indexToNumQ
                if( i>= queries[j][0] && i<=queries[j][1] ) {
                    arr.push_back(queries[j][2]);
                    indexToNumQ.push_back(j+1);
                }
            }
            if( arr.size() == 0 )
                return -1;
            vector<vector<int>> dp(n+1,vector<int>(arr.size(),-1));
            int x = solve( n, 0, arr, dp );
            if( x == MAX )
                return -1;
            final[i] = indexToNumQ[ x ];
        }
        int ans=-1;
        for( auto i : final )
            ans = max(ans,i);
        return ans;
    }
};

