class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int ans = -1;
        for(int i = 0; i<n; i++) {
            int q = queries.size();
            vector<vector<int>> dp(q, vector<int> (nums[i]+1, -1));
            int temp = dfs(queries, i, 0, nums[i], dp);
            ans = max(ans, temp);
        }
        return ans == INT_MAX ? -1 : ans;
    }

    int dfs(vector<vector<int>>& queries, int index, int start, int target, vector<vector<int>> &dp) {
        if (target == 0)
            return start;
        if (target < 0 || start == queries.size())
            return INT_MAX;

        if (dp[start][target] != -1)
            return dp[start][target];

        int temp = INT_MAX;
        if (queries[start][0] <= index && queries[start][1] >= index)
            temp = dfs(queries, index, start + 1, target - queries[start][2], dp);
        return dp[start][target] = min(dfs(queries, index, start + 1, target, dp), temp);
    }
};
