class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if(allZeroes(nums))
            return 0;
        ArrayList<ArrayList<Integer>> list = new ArrayList<>();
        int n = nums.length;
        for(int i=0;i<n;i++)
            list.add(new ArrayList<>());
        for(int i=0;i<queries.length;i++) {
            int start = queries[i][0], end = queries[i][1];
            for(int j=start;j<=end;j++)
                list.get(j).add(queries[i][2]);
            if(isPossible(nums, list))
                return i+1;
        }
        return -1;
    }

    private boolean isPossible(int[] nums, ArrayList<ArrayList<Integer>> list) {
        int n = nums.length;
        for(int i=0;i<n;i++) {
            if(nums[i] == 0)
                continue;
            int m = list.get(i).size();
            int[][] dp = new int[m][nums[i]+1];
            for(int[] rows : dp)
                Arrays.fill(rows,-1);
            if(check(list.get(i),0, nums[i],dp) == 0)
                return false;
        }
        return true;
    }

    private int check(ArrayList<Integer> list, int i, int sum, int[][] dp) {
        int n = list.size();
        if(sum == 0)
            return 1;
        if(sum<0 || i == n)
            return 0;
        if(dp[i][sum] != -1)
            return dp[i][sum];
        int ans = 0;
        ans |= check(list,i+1,sum-list.get(i),dp);
        ans |= check(list,i+1,sum,dp);
        return dp[i][sum] = ans;
    }

    private boolean allZeroes(int[] nums) {
        for(int num : nums) {
            if(num>0)
                return false;
        }
        return true;
    }
}

