            return 0;
        }
        int tam=queries.size(),n=nums.size();
        vector<vi> values(n);
        FOR(i,0,tam){
            FOR(it,queries[i][0],queries[i][1]+1){
                values[it].pb(queries[i][2]);
            }
            bool ok=true;
            FOR(j,0,n){
                int len=values[j].size();
                vvi dp(len,vi(nums[j]+1,0));
                if(len>0){
                    dp[0][0]=1;
                    if(values[j][0]<=nums[j])
                        dp[0][values[j][0]]=1;
                }
                FOR(k,1,len){
                    FOR(it,0,nums[j]+1){
                        dp[k][it]=dp[k-1][it];
                        if(it >= values[j][k] && dp[k-1][it-values[j][k]]){
                            dp[k][it]=1;
                        }
                    }
                }
                if(len==0){
                    if(nums[j]!=0){
                        ok=false;
                        break;
                    }
                }
                if(len>=1 && !dp[len-1][nums[j]]){
                    ok=false;
                    break;
                }
            }
            if(ok){
                return i+1;
            }
        }
        return -1;
    }
};

