class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        bool flag = true;
        for(auto &num:nums){
            if(num != 0){
                flag = false;
                break;
            }
        }
        if(flag) return 0;

        vector<bitset<1001>> bits(n,bitset<1001>(0));
        for(int i=0;i<n;i++){
            bits[i].set(0,1);
        }

        for(int i=0;i<queries.size();i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for(int j=l;j<=r;j++){
                bits[j] |= (bits[j] << val);
                for(int k = nums[j]+1;k<1001;k++){
                    bits[j].reset(k);
                }
            }

            bool allZero = true;
            for(int j=0;j<n;j++){
                if(bits[j][nums[j]] == 0){
                    allZero = false;
                    break;
                }
            }
            if(allZero) return i+1;
        }
        return -1;

    }
};

