class Solution {
public:
    int isZeroArray(int n, vector<bitset<1001>> &dp, vector<int> &nums){
        for (int i = 0; i < n; ++i)
            if (!dp[i].test(nums[i])) return false;
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        vector<bitset<1001>> dp(n);
        for (int i = 0; i < n; ++i) dp[i].set(0);

        if (isZeroArray(n, dp, nums)) return 0;
        for (int i = 0; i < q; ++i) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; ++j)
                dp[j] |= (dp[j] << val);
            if (isZeroArray(n, dp, nums)) return i + 1;
        }
        return -1;
    }
};

