class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;
        int qsz = queries.size();
        int nsz = nums.size();


        for(int i=0; i < nsz; ++i){
            vector<int> vals;

            for(int j=0; j < qsz; ++j){
                if(i <= queries[j][1] && i >= queries[j][0] && 
                    nums[i] >= queries[j][2])
                {
                    vals.push_back(queries[j][2]);
                }
                else{
                    vals.push_back(0);
                }
            }

            // cout << "Printing vals:n";
            // for(auto& v: vals){
            //     cout << v << " ";
            // }
            // cout << "n";

            // now minimum number of starting vals to process to make nums[i] -> 0
            // generally we pick or not pick each val and check next
            // but we see repeating subproblems -> so make dp

            int tempAns = INT_MAX;

            vector<int> dp(nums[i]+1, INT_MAX);
            dp[0] = 0;

            for(int j=0; j < qsz && dp[nums[i]] == INT_MAX; ++j){
                if(vals[j] == 0){
                    continue;
                }
                else{
                    for(int k = nums[i]; k >= vals[j]; --k){
                        if(dp[k] == INT_MAX && dp[k-vals[j]] != INT_MAX){
                            dp[k] = j+1; //need to process j+1 queries
                        }
                    }
                }
            }

            // cout << "dp[nums[i]] : " << dp[nums[i]] << endl;
            if(dp[nums[i]] != INT_MAX){
                // we achieved something
                ans = max(ans, dp[nums[i]]);
            }
            else{
                return -1;
            }
        }

        return ans; //we processed all elements

    }
};

