class Solution {
public:
    bool _knapsack(const vector<int> &a, int v) {
        int m = a.size();
        vector<bool> f(vector<bool>(v+1, false));
        f[0] = true;
        for (int i = 0; i < m; ++i){
            for (int j = v; j >= a[i]; --j){
                f[j] = (f[j] || f[j-a[i]]);
            }
        }
        return f[v];
    }

    bool check(int k, const vector<int>& nums, const vector<vector<int>>& queries){
        for (int i = 0; i < nums.size(); ++i){
            vector<int> a(0);
            for (int j = 0; j < k; ++j){
                auto q = queries[j];
                int l = q[0], r = q[1], val = q[2];
                if (i < l || i > r) continue;
                a.push_back(val);
            }
            if (_knapsack(a, nums[i]) == false) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size();
        int k = -1;
        while (l <= r){
            int m = (l+r)/2;
            if (check(m, nums, queries)) {
                k = m;
                r = m-1;
            } else l = m+1;
        }
        return k;
        // for (int i = l; i <= r; ++i){
        //     if (check(i, nums, queries)) return i;
        // }
        // return -1;
    }
};

