class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        vector<unordered_set<int> > v(n);

        for (int i = 0; i < n; i++) {
            v[i].insert(nums[i]);
        }

        int cnt = 0;
        for (auto st: v) {
            if (st.find(0) != st.end()) {
                cnt++;
            }
        }
        if (cnt == n) return 0;

        for (int i = 0; i < m; i++) {
            vector<int> q = queries[i];
            int l = q[0], r = q[1], val = q[2];

            for (int j = l; j <= r; j++) {
                if (v[j].find(0) != v[j].end()) continue;

                vector<int> tmp;
                for (int e: v[j]) {
                    if (v[j].find(e - val) == v[j].end()) {
                        tmp.push_back(e - val);
                    }
                }

                for (int e: tmp) {
                    v[j].insert(e);
                }
            }

            int cnt = 0;
            for (auto st: v) {
                if (st.find(0) != st.end()) {
                    cnt++;
                }
            }
            if (cnt == n) return i + 1;
        }

        return -1;
    }
};

