#include <stdio.h>
#include <stdlib.h>
#include <stdbool.h>

#define MAX_N 1000  // Based on problem constraints

// Function to check if all elements in the array are zero
bool allZero(int* nums, int numsSize) {
    for (int i = 0; i < numsSize; i++) {
        if (nums[i] != 0) return false;
    }
    return true;
}

// Function to perform the Zero Array Transformation
int minZeroArray(int* nums, int numsSize, int** queries, int queriesSize, int* queriesColSize) {
    if (allZero(nums, numsSize)) return 0; // If all are already zero, return 0

    // 2D DP table: `dp[i][j]` tracks if `nums[i]` can become `j`
    bool dp[MAX_N][MAX_N + 1] = {false}; 

    // Initialize DP table
    for (int i = 0; i < numsSize; i++) {
        dp[i][0] = true;  // Zero is always achievable
    }

    // Process queries
    for (int k = 0; k < queriesSize; k++) {
        int l = queries[k][0];
        int r = queries[k][1];
        int v = queries[k][2];

        for (int i = l; i <= r; i++) {
            // Shift DP values for index `i`
            for (int j = MAX_N; j >= v; j--) {
                if (dp[i][j - v]) dp[i][j] = true;
            }

            // Clear values beyond nums[i]
            for (int j = nums[i] + 1; j <= MAX_N; j++) {
                dp[i][j] = false;
            }
        }

        // Check if all nums[i] can be achieved
        bool allAchieved = true;
        for (int i = 0; i < numsSize; i++) {
            if (!dp[i][nums[i]]) {
                allAchieved = false;
                break;
            }
        }

        if (allAchieved) return k + 1;
    }

    return -1; // Not possible to transform the array to all zeroes
}

