class Solution {
public:
    int gettingAnswer(vector<int> &numbers, vector<vector<int>> &operations)
    {
        int arraySize = numbers.size();
        vector<bitset<1001>> possibleValues(arraySize);

        for (int index = 0; index < arraySize; index++) {
            possibleValues[index].set(0);
        }

        if (all_of(numbers.begin(), numbers.end(), [](int x) { return x == 0; })) {
            return 0;
        }

        for (int operationIndex = 0; operationIndex < operations.size(); operationIndex++) {
            int startPos = operations[operationIndex][0];
            int endPos = operations[operationIndex][1];
            int addValue = operations[operationIndex][2];

            for (int pos = startPos; pos <= endPos; pos++) {
                bitset<1001> shiftedValues;

                for (int currentVal = 0; currentVal < 1001 - addValue; currentVal++) {
                    if (possibleValues[pos][currentVal]) {
                        shiftedValues.set(currentVal + addValue);
                    }
                }

                possibleValues[pos] |= shiftedValues;

                for (int val = numbers[pos] + 1; val < 1001; val++) {
                    possibleValues[pos].reset(val);
                }
            }

            bool targetAchieved = true;
            for (int index = 0; index < arraySize; index++) {
                if (!possibleValues[index][numbers[index]]) {
                    targetAchieved = false;
                    break;
                }
            }

            if (targetAchieved) {
                return operationIndex + 1;
            }
        }

        return -1;
    }

    int minZeroArray(vector<int>& numbers, vector<vector<int>>& operations) 
    {
        return gettingAnswer(numbers, operations);
    }
};

