            for (int z = a; z<= b; z++) {
                bitset<1001> change;
                for (int i = 0; i < 1001 - c; i++) {
                    if (dp[z][i]) {
                        change.set(i + c);
                    }
                }
                dp[z] |= change;

                for (int pos = nums[z] + 1; pos < 1001; pos++) {
                    dp[z].reset(pos);
                }
            }

            bool m = true;
            for (int h = 0; h < sizeofnums; h++) {
                if (!dp[h][nums[h]]) {
                    m = false;
                    break;
                }
            }

            if (m) {
                return p + 1;
            }
        }
        return -1;
    }
};

