class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def check(k):
            if k == m:
                return False
            for idx, num in enumerate(nums):
                if num == 0:
                    continue
                if not subset_sum(k, num, idx):
                    return False
            return True
        def subset_sum(k, x, idx):
            dp = [False] * (x + 1)
            dp[0] = True
            for l, r, num in queries[:k+1]:
                if l <= idx <= r: 
                    for current_sum in range(x, num-1, -1):
                        if dp[current_sum - num]:
                            dp[current_sum] = True
            return dp[x]
        n = len(nums)
        m = len(queries)
        if sum(nums) == 0:
            return 0
        l, r = 0, m
        while l < r:
            mid = (l+r)//2
            if check(mid):
                r = mid
            else:
                l = mid+1
        if l == m:
            return -1
        return l+1

